mapper.insert:
INSERT INTO BOOK_CLUB (  
    CLUB_CODE
    , CLUB_NAME
    , CLUB_HEAD_CNT
    , CLUB_INTRO
    , MEM_ID
    , MEM_NAME
) VALUES (
    (SELECT 'CLUB_'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(CLUB_CODE, 6))), 0) + 1, 3, 0) AS CLUB_CODE FROM BOOK_CLUB)
    , #{clubName}
    , #{clubHeadCnt}
    , #{clubIntro}
    , #{memId}
    , #{memName}
)

mapper.update:
UPDATE BOOK_CLUB
SET
CLUB_NAME = #{clubName}
, CLUB_HEAD_CNT = #{clubHeadCnt}
, CLUB_INTRO = #{clubIntro}
WHERE CLUB_CODE = #{clubCode}

mapper.insert:
INSERT INTO BOOK_CLUB_BOARD (
	CB_BOARD_NUM
	, CB_BOARD_TITLE
	, CB_BOARD_CONTENT
	, CLUB_CODE
	, MEM_ID
	, MEM_NAME
) VALUES (
	(SELECT 'CB_BOARD_'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(CB_BOARD_NUM, 10))), 0) + 1, 3, 0) AS CB_BOARD_NUM FROM BOOK_CLUB_BOARD)
	, #{cbBoardTitle}
	, #{cbBoardContent}
	, #{clubCode}
	, #{memId}
	, #{memName}
)

mapper.select:
SELECT CB_BOARD_NUM , CB_BOARD_TITLE , CB_BOARD_DATE , MEM_ID , CLUB_CODE , MEM_NAME , CB_CMT_COUNT , CB_READ_CNT , ROW_NUM FROM ( SELECT CB_BOARD_NUM , CB_BOARD_TITLE , CB_BOARD_DATE , MEM_ID , CLUB_CODE , MEM_NAME , CB_CMT_COUNT , CB_READ_CNT , ROWNUM AS ROW_NUM FROM ( SELECT CB_BOARD_NUM , CB_BOARD_TITLE , TO_CHAR(CB_BOARD_DATE, 'YYYY-MM-DD') AS CB_BOARD_DATE , MEM_ID , CLUB_CODE , MEM_NAME , (SELECT COUNT(CB_CMT_NUM) FROM BOOK_CLUB_BOARD_COMMENT WHERE CB_BOARD_NUM = BOOK_CLUB_BOARD.CB_BOARD_NUM) AS CB_CMT_COUNT , CB_READ_CNT FROM BOOK_CLUB_BOARD WHERE CLUB_CODE = #{clubCode}
AND ${sort} LIKE '%'||#{keyword}||'%'
ORDER BY CB_BOARD_NUM DESC ) ) WHERE ROW_NUM > = #{startNum} AND ROW_NUM < = #{endNum}

mapper.update:
UPDATE BOOK_CLUB_BOARD 
SET
CB_BOARD_TITLE = #{cbBoardTitle}
, CB_BOARD_CONTENT = #{cbBoardContent}
WHERE CB_BOARD_NUM = #{cbBoardNum}
AND CLUB_CODE = #{clubCode}

mapper.delete:
DELETE BOOK_CLUB_BOARD
WHERE CB_BOARD_NUM = #{cbBoardNum}
AND CLUB_CODE = #{clubCode}

mapper.insert:
INSERT INTO BOOK_CLUB_BOARD_COMMENT (
	CB_CMT_NUM
	, CB_CMT_CONTENT
	, MEM_NAME
	, MEM_ID
	, CB_BOARD_NUM
	, CLUB_CODE
) VALUES (
	(SELECT 'CBCMT_'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(CB_CMT_NUM, 7))), 0) + 1, 3, 0) AS CB_CMT_NUM FROM BOOK_CLUB_BOARD_COMMENT)
	, #{cbCmtContent}
	, #{memName}
	, #{memId}
	, #{cbBoardNum}
	, #{clubCode}
)

mapper.update:
UPDATE BOOK_CLUB_BOARD_COMMENT
SET
CB_CMT_CONTENT = #{cbCmtContent}
WHERE CB_CMT_NUM = #{cbCmtNum}

mapper.insert:
INSERT INTO BOOK_CLUB_APPLY (
	CLUB_APPLY_CODE
	, CLUB_CODE
	, CLUB_NAME
	, MEM_ID
	, MEM_NAME
) VALUES (
	(SELECT 'APPLY_'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(CLUB_APPLY_CODE, 7))), 0) + 1, 3, 0) AS CLUB_APPLY_CODE FROM BOOK_CLUB_APPLY)
	, #{clubCode}
	, #{clubName}
	, #{memId}
	, #{memName}
)

mapper.select:
SELECT CLUB_APPLY_CODE
FROM BOOK_CLUB_APPLY
WHERE MEM_ID = #{memId}

mapper.update:
UPDATE BOOK_MEMBER
SET
CLUB_CODE = #{clubCode}
WHERE MEM_ID = #{memId}

mapper.select:
select count(CB_CMT_NUM)
FROM BOOK_CLUB_BOARD_COMMENT
WHERE CB_BOARD_NUM = #{cbBoardNum}

mapper.insert:
INSERT INTO MESSAGE (
	MSG_CODE
	, GET_ID
	, MSG_CONTENT
) VALUES (
	(SELECT 'MSG_'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(MSG_CODE, 5))), 0) + 1, 3, 0) AS MSG_CODE FROM MESSAGE)
	, #{getId}
	, '신청한 북클럽에 가입되셨습니다. 북클럽 게시판에 첫 인사를 올려보세요!'
)
